contract = web3.eth.contract(address=pmp_contract, abi=pmp_abi)

account = web3.eth.account.create('secret_passphrase')

def getMaxSupply() -> int:
    supply = contract.functions.totalSupply().call({'from': account.address})
    return int(supply)